import socket
import struct
import time

message = ""
multicast_group = ("224.3.29.1", 10001)
TTL = struct.pack('b', 8)
sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_TTL, TTL)
while True:
    sock.sendto(message.encode(), multicast_group)
    time.sleep(3)